def custom_export(players):
    yield ['participant_code', 'id_in_group', 'payment']
    for p in players:
        pp = p.participant
        yield [pp.code, p.id_in_group, pp.payment]
class Demographics(Page):
    form_model = 'player'
    form_fields = ['check1', 'check2', 'reasons', 'nationality', 'other_country', 'time_spent_other_country', 'gender', 'age', 'job', 'work_experience', 'religion']
class Payment(Page):
    form_model = 'player'
    @staticmethod
    def vars_for_template(player: Player):
        participant = player.participant
        return dict(payoff = player.participant.payoff_plus_participation_fee())
page_sequence = [Demographics, Payment]